// The GraphQL query recommended for a full schema introspection.
export {
  getIntrospectionQuery,
  // @deprecated, use getIntrospectionQuery() - will be removed in v15
  introspectionQuery,
} from 'graphql/utilities/introspectionQuery';

export {
  IntrospectionOptions,
  IntrospectionQuery,
  IntrospectionSchema,
  IntrospectionType,
  IntrospectionInputType,
  IntrospectionOutputType,
  IntrospectionScalarType,
  IntrospectionObjectType,
  IntrospectionInterfaceType,
  IntrospectionUnionType,
  IntrospectionEnumType,
  IntrospectionInputObjectType,
  IntrospectionTypeRef,
  IntrospectionInputTypeRef,
  IntrospectionOutputTypeRef,
  IntrospectionNamedTypeRef,
  IntrospectionListTypeRef,
  IntrospectionNonNullTypeRef,
  IntrospectionField,
  IntrospectionInputValue,
  IntrospectionEnumValue,
  IntrospectionDirective,
} from 'graphql/utilities/introspectionQuery';

// Gets the target Operation from a Document
export { getOperationAST } from 'graphql/utilities/getOperationAST';

// Gets the Type for the target Operation AST.
export { getOperationRootType } from 'graphql/utilities/getOperationRootType';

// Convert a GraphQLSchema to an IntrospectionQuery
export { introspectionFromSchema } from 'graphql/utilities/introspectionFromSchema';

// Build a GraphQLSchema from an introspection result.
export { buildClientSchema } from 'graphql/utilities/buildClientSchema';

// Build a GraphQLSchema from GraphQL Schema language.
export {
  buildASTSchema,
  buildSchema,
  // @deprecated: Get the description from a schema AST node and supports legacy
  // syntax for specifying descriptions - will be removed in v16
  getDescription,
  BuildSchemaOptions,
} from 'graphql/utilities/buildASTSchema';

// Extends an existing GraphQLSchema from a parsed GraphQL Schema language AST.
export { extendSchema } from 'graphql/utilities/extendSchema';

// Sort a GraphQLSchema.
export { lexicographicSortSchema } from 'graphql/utilities/lexicographicSortSchema';

// Print a GraphQLSchema to GraphQL Schema language.
export {
  printSchema,
  printType,
  printIntrospectionSchema,
} from 'graphql/utilities/schemaPrinter';

// Create a GraphQLType from a GraphQL language AST.
export { typeFromAST } from 'graphql/utilities/typeFromAST';

// Create a JavaScript value from a GraphQL language AST with a type.
export { valueFromAST } from 'graphql/utilities/valueFromAST';

// Create a JavaScript value from a GraphQL language AST without a type.
export { valueFromASTUntyped } from 'graphql/utilities/valueFromASTUntyped';

// Create a GraphQL language AST from a JavaScript value.
export { astFromValue } from 'graphql/utilities/astFromValue';

// A helper to use within recursive-descent visitors which need to be aware of
// the GraphQL type system.
export { TypeInfo } from 'graphql/utilities/TypeInfo';

// Coerces a JavaScript value to a GraphQL type, or produces errors.
export { coerceInputValue } from 'graphql/utilities/coerceInputValue';

// Coerces a JavaScript value to a GraphQL type, or produces errors.
export { coerceValue } from 'graphql/utilities/coerceValue';

// @deprecated use coerceValue - will be removed in v15
export { isValidJSValue } from 'graphql/utilities/isValidJSValue';

// @deprecated use validation - will be removed in v15
export { isValidLiteralValue } from 'graphql/utilities/isValidLiteralValue';

// Concatenates multiple AST together.
export { concatAST } from 'graphql/utilities/concatAST';

// Separates an AST into an AST per Operation.
export { separateOperations } from 'graphql/utilities/separateOperations';

// Strips characters that are not significant to the validity or execution
// of a GraphQL document.
export { stripIgnoredCharacters } from 'graphql/utilities/stripIgnoredCharacters';

// Comparators for types
export {
  isEqualType,
  isTypeSubTypeOf,
  doTypesOverlap,
} from 'graphql/utilities/typeComparators';

// Asserts that a string is a valid GraphQL name
export { assertValidName, isValidNameError } from 'graphql/utilities/assertValidName';

// Compares two GraphQLSchemas and detects breaking changes.
export {
  BreakingChangeType,
  DangerousChangeType,
  findBreakingChanges,
  findDangerousChanges,
  BreakingChange,
  DangerousChange,
} from 'graphql/utilities/findBreakingChanges';

// Report all deprecated usage within a GraphQL document.
export { findDeprecatedUsages } from 'graphql/utilities/findDeprecatedUsages';
